USE `mydb`$$
DROP TRIGGER IF EXISTS `mydb`.`Connessione_BEFORE_INSERT` $$
USE `mydb`$$
CREATE DEFINER = CURRENT_USER TRIGGER `mydb`.`Connessione_BEFORE_INSERT` BEFORE INSERT ON `Connessione` FOR EACH ROW
BEGIN
	if(	(select s.disponibile
		from Ip s
